    function like(elm) {

        elm.classList.toggle('active');

        elm.closest('.likes').querySelector('.likes-svg-dislike').classList.remove('active');

    }

    function dislike(elm) {

        elm.classList.toggle('active');

        elm.closest('.likes').querySelector('.likes-svg-like').classList.remove('active');

    }

</script>
   
